CREATE TABLE "public"."cart" ("id" uuid NOT NULL DEFAULT uuid_generate_v4(), "user_id" uuid NOT NULL, "item_id" uuid NOT NULL, "quantity" integer NOT NULL DEFAULT 1, "created_at" timestamp with time zone DEFAULT now(), "updated_at" timestamp with time zone DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."user"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("item_id") REFERENCES "public"."item"("id") ON UPDATE restrict ON DELETE cascade, UNIQUE ("id"), UNIQUE ("user_id", "item_id"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
